'use strict';
import {
    Dimensions,
    PixelRatio

} from 'react-native';
var Device = Dimensions.get('window');

var screenWidth = Device.width;
var designWidthPx = 750.0;
var desginPixelRatio = 2;

var Aspect = {

    deviceWidth: function(){
        return screenWidth;
    },

    deviceHeight: function(){
        return  Device.height;
    },

    ratio: function(num) {
        return Math.round((num * desginPixelRatio / designWidthPx) * Device.width);
    },

    ratioPixel: function(num) {
        return num / PixelRatio.get();
    },
    
};

Aspect.onePixel = Aspect.ratioPixel(1);

module.exports =  Aspect;
