'use strict';
var Common = {

    delay: function(time) {
        return new Promise((resolve) => setTimeout(resolve, time))
    },

    isPromise:function (obj) {
        return !!obj && (typeof obj === 'object' || typeof obj === 'function') && typeof obj.then === 'function';
    }
};

module.exports = Common;
