'use strict';

import React from 'react';
import {
    ActivityIndicator,
    ColorPropType,
} from 'react-native';

import OrionView from '../view/view';

const OrionActivityIndicator = React.createClass({
    propTypes: {
        ...OrionView.propTypes,
        animating: PropTypes.bool,
        color: ColorPropType,
        size: PropTypes.oneOf([
            'small',
            'large',
        ]),
    },
    getDefaultProps: function() {
        return {
            ...OrionView.defaultProps,
            animating: true,
            color: 'gray',
            size: 'small',
        };
    },
    render: function () {
        return (
            <ActivityIndicator {...this.props}/>
        );
    },
});

module.exports = OrionActivityIndicator;
