'use strict';

import React from 'react';
import {
    ColorPropType,
} from 'react-native';

import OrionView from '../view/view';


const OrionActivityIndicatorPropType = React.PropTypes.shape({
    ...OrionView.propTypes,
    animating: PropTypes.bool,
    color: ColorPropType,
    size: PropTypes.oneOf([
        'small',
        'large',
    ]),
});

module.exports = OrionActivityIndicatorPropType;
