'use strict';

import React from 'react';
import {
    Platform,
    Alert,
    AlertIOS,
} from 'react-native';

import OrionComponent from '../component';

const OrionAlert = {
 
    alert: function (title, msg, buttons ) {

        if( Platform.OS === 'ios' ){
            AlertIOS.alert(title,msg,buttons);
        }
        else{
            Alert.alert(title,msg, buttons);
        }
    },
    
    question: function (title,msg,ok,cancel) {
        var buttons=[
            {text: 'OK', onPress: ok},
            {text: 'Cancel', onPress: cancel, style: 'cancel'},
        ];
        this.alert(title,msg, buttons);
    }
};

module.exports = OrionAlert;
