'use strict';

import React from 'react';
import {
    StyleSheet,
    View,
} from 'react-native';

import OrionView from  '../view/view';

var OrionGridViewPlaceHolder = React.createClass({
    propTypes:{
        ...OrionView.propTypes,
    },
    getDefaultProps: function() {
        return {
            ...OrionView.defaultProps,
        };
    },

    componentDidMount:function(){

    },

    render: function() {
        var containerProps = {
                ref: 'container',
                style: [this.props.style, styles.container]
            };
        return (
            <View {...containerProps}/>
        );
    },
});

var styles = StyleSheet.create({
    container:{
        flex:1,
    }
});

module.exports = OrionGridViewPlaceHolder;
