'use strict';

import React from 'react';
import {
    Image,
} from 'react-native';

import OrionComponent from '../component';

const OrionImage = React.createClass({
    propTypes: {
        ...OrionComponent.propTypes,
        ...Image.propTypes,
        l_uri: React.PropTypes.string,
    },
    getDefaultProps: function() {
        return {
            ...OrionComponent.defaultProps,
            ...Image.defaultProps,
        };
    },
    render: function () {
        let {l_uri, source, ...props} = this.props;
        var src;
        if( typeof source !== 'number'){
            if( l_uri  && l_uri != '' ){
                src = {...source};
                src.uri = l_uri;
            }
        }
        else{
            src = source;
        }

        return (
            <Image {...props} source={src}/>
        );
    },
});

module.exports = OrionImage;
