'use strict';

import React from 'react';
import {
    StyleSheet,
    View,
    ColorPropType,
} from 'react-native';

import OrionComponent from '../component';
import OrionImage from './image';
import OrionImagePropType from './imagePropType';

const OrionMaskImage = React.createClass({
    propTypes: {
        ...OrionComponent.propTypes,
        ...OrionImage.propTypes,
        l_maskColor: ColorPropType,
        l_maskOpacity: React.PropTypes.number,
    },
    getDefaultProps: function() {
        return {
            ...OrionComponent.defaultProps,
            ...OrionImage.defaultProps,
        };
    },
    render: function () {
        let {l_maskColor, l_maskOpacity,  ...props} = this.props;

        var mask;
        if( l_maskColor && l_maskOpacity ){
            mask = (
                <View style={[styles.mask, {backgroundColor:l_maskColor, opacity: l_maskOpacity}]}/>
            );
        }

        return (
            <View style={styles.container}>
                <OrionImage {...props}/>
                {mask}
            </View>
        );
    },
});

var styles = StyleSheet.create({
    container:{
    },
    mask:{
        position:'absolute',
        left:0,
        right:0,
        top:0,
        bottom:0,
    }
});

module.exports = OrionMaskImage;
