'use strict';

import React from 'react';
import {
    StyleSheet,
    StyleSheetPropType,
    Image,
    View,
    ViewAttributes,
    requireNativeComponent,
} from 'react-native';

import OrionComponent from '../component';
import TimerMixin from '../../timermixin';

const OrionWebImage = React.createClass({
  mixins: [TimerMixin],

  propTypes: {
    ...OrionComponent.propTypes,
    style: Image.propTypes.style,
    url: React.PropTypes.string,
    fillMode: React.PropTypes.oneOf(['fill','aspectfit','aspectfill']),
  },

  viewConfig: {...View.viewConfig},

  getDefaultProps: function() {
    return {
      ...OrionComponent.defaultProps,
      fillMode: 'aspectfill',
    };
  },

  render: function () {
    let {url, fillMode, style, ...props} = this.props;

    return (
        <RCTSDWebImageView
            {...props}
            style={[styles.base, style]}
            url={url}
            fillMode={fillMode}
        />
    );
  },
});

var styles = StyleSheet.create({
  base: {
    overflow: 'hidden',
  },
});

var RCTSDWebImageView = requireNativeComponent('RCTSDWebImageView', OrionWebImage);

module.exports = OrionWebImage;



