'use strict';

import React from 'react';

import {
    ListView,
    ColorPropType,
    RefreshControl,
} from 'react-native';

import OrionComponent from '../component';

const OrionListView = React.createClass({
    propTypes: {
        ...OrionComponent.propTypes,
        ...ListView.propTypes,
        enableRefresh: React.PropTypes.bool,
    },

    getDefaultProps: function() {
        return {
            ...OrionComponent.defaultProps,
            ...ListView.defaultProps,
            enableRefresh:false,
        };
    },
    
    render: function () {
        var {refreshControl, ...props} = this.props;
        return (
            <ListView {...props} refreshControl={this.props.enableRefresh ? refreshControl : null}/>
        );
    },
    

});

OrionListView.cloneRows = function (data) {
    var ds = new ListView.DataSource({rowHasChanged: (r1, r2) => r1 !== r2});
    if( !data || data.length <= 0 ){
        return null;
    }
    return ds.cloneWithRows(data);
};

module.exports = OrionListView;
