'use strict';

import React from 'react';
import {
    StyleSheet,
    View,
} from 'react-native';

import OrionComponent from '../component';
import OrionIconFont from '../text/iconfont';
import OrionText from '../text/text';

const OrionNavigatorIconFontButton = React.createClass({
    propTypes: {
        ...OrionComponent.propTypes,
        style: View.propTypes.style,
        iconStyle: OrionIconFont.propTypes.style,
        iconFontFamily: React.PropTypes.string,
        iconCode: React.PropTypes.string,
        titleStyle: OrionText.propTypes.style,
        title: React.PropTypes.string,
        layout: React.PropTypes.oneOf(['left','right']),
    },
    getDefaultProps: function() {
        return {
            ...OrionComponent.defaultProps,
            layout: 'left',
        };
    },
    render: function () {
        var {layout, iconStyle, iconFontFamily, iconCode, titleStyle, title, style,...props} = this.props;
        var content;

        var icon_n = ( iconCode &&  iconFontFamily )? (<OrionIconFont  style={[styles.icon, iconStyle]} l_font={iconFontFamily} l_code={iconCode}/>) : null;
        var title_n = title ? (<OrionText  style={[styles.title, titleStyle]} l_text={title} />) : null;

        if( layout === 'left') {
            content = (
                <View {...props} style={[styles.container, style]}>
                    {icon_n}
                    {title_n}
                </View>
            );
        }
        else{
            content = (
                <View {...props} style={[styles.container, style]}>
                    {icon_n}
                    {title_n}
                </View>
            );
        }
        return content;
    },

});

let styles = StyleSheet.create({
    container:{
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center',
        //marginTop: 10,
    },
    icon:{
        fontSize: 17,
        color: '#ffffff',
    },
    title: {
        textAlign: 'center',
        fontSize: 17,
        color: '#ffffff',
    },
});

module.exports = OrionNavigatorIconFontButton;
