import React, { Component } from 'react';
import {
    Platform,
    StyleSheet,
    NavigatorIOS,
    ColorPropType,
} from 'react-native';

import {
    Aspect,
    OrionComponent,
} from 'react-native-lark';

const OrionNavigator = React.createClass({
    propTypes: {
        ...OrionComponent.propTypes,
        ...NavigatorIOS.propTypes,
    },
    getDefaultProps: function() {
        return {
            ...OrionComponent.defaultProps,
            ...NavigatorIOS.defaultProps,
        };
    },
    render:function (){
        let{ children, ...props } = this.props;
        return (
            <NavigatorIOS {...this.props}>{children}
            </NavigatorIOS>
        );
    }
});

module.exports = OrionNavigator;
