'use strict';

import React from 'react';
import {
    ScrollView
} from 'react-native';

import OrionComponent from '../component';

const OrionScrollView = React.createClass({
    propTypes: {
        ...OrionComponent.propTypes,
        ...ScrollView.propTypes,
    },
    getDefaultProps: function() {
        return {
            ...OrionComponent.defaultProps,
            ...ScrollView.defaultProps,
        };
    },
    render: function () {
        return (
            <ScrollView {...this.props}>{this.props.children}</ScrollView>
        );
    },
});

module.exports = OrionScrollView;
