'use strict';

import React from 'react';

const OrionStaticContainer = React.createClass({
  propTypes:{
    shouldUpdate: React.PropTypes.bool,
  },

  shouldComponentUpdate: function(nextProps: Object){
    return !!nextProps.shouldUpdate;   // 转换为布尔型
  },

  render: function () {
    let { children } = this.props;
    return children ? React.Children.only(children) : null;  // 仅渲染唯一子节点(多个子节点导致抛出异常)
  }
});

module.exports = OrionStaticContainer;
