'use strict';

import React from 'react';
import {
    Animated,
    Platform,
    StyleSheet,
    View,
} from 'react-native';

import Layout from './layout';

const OrionTab  = React.createClass({
    propTypes : {
        ...Animated.View.propTypes,
    },
    render() {
        let {style, ...props} = this.props;
        return (
            <Animated.View {...props} style={[this.props.style,styles.container]}>
                {this.props.children}
            </Animated.View>
        );
    }
});

let styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        height: Layout.tabBarHeight,
        position: 'absolute',
        bottom: 0,
        left: 0,
        right: 0,
    },
});

module.exports = OrionTab;