'use strict';

import React from 'react';
import {
    Text
} from 'react-native';

import OrionComponent from '../component';

const OrionIconFont = React.createClass({
    propTypes: {
        ...OrionComponent.propTypes,
        ...Text.propTypes,
        l_font: React.PropTypes.string,
        l_code: React.PropTypes.string,
    },
    getDefaultProps: function() {
        return {
            ...OrionComponent.defaultProps,
            ...Text.defaultProps,
            l_code:'',
        };
    },
    render: function () {
        var {l_font,l_code, style, ...props} = this.props;
        var fstyle = {};
        if( l_font ){
            fstyle.fontFamily = l_font;
        }
        var icon = String.fromCharCode(l_code);
        return (
            <Text {...props} style={[style,fstyle]}>{icon}</Text>
        );
    },
});

module.exports = OrionIconFont;
