'use strict';

import React from 'react';
import {
    Text
} from 'react-native';

import OrionComponent from '../component';

const OrionText = React.createClass({
    propTypes: {
        ...OrionComponent.propTypes,
        ...Text.propTypes,
        l_text: React.PropTypes.string,
    },
    getDefaultProps: function() {
        return {
            ...OrionComponent.defaultProps,
            ...Text.defaultProps,
            l_text:'',
        };
    },
    render: function () {
        return (
            <Text {...this.props}>{this.props.l_text}</Text>
        );
    },
});

module.exports = OrionText;
