'use strict';

import React from 'react';
import {
    WebView
} from 'react-native';

import OrionComponent from '../component';

const OrionWebView = React.createClass({
    propTypes: {
        ...OrionComponent.propTypes,
        ...WebView.propTypes,
        l_url: React.PropTypes.string,
    },
    getDefaultProps: function() {
        return {
            ...OrionComponent.defaultProps,
            ...WebView.defaultProps,
        };
    },
    render: function () {
        var {l_url, source, ...props} = this.props;
        var nsource = {...source};
        if( l_url ){
            nsource.uri = l_url;
        }
        return (
            <WebView {...props} source={nsource}/>
        );
    },
});

module.exports = OrionWebView;
